package com.wipro.FlowControlStatements;

public class Ex4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char A='s',B='e';
		if(A<B)
		System.out.println(A+","+B);
		else
		System.out.println(B+","+A);
		char C='a',D='e';
		if(C<D)
		System.out.println(C+","+D);
		else
		System.out.println(D+","+C);
	}

}
